import CartItemForm from './CartItemForm';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import Stack from 'react-bootstrap/Stack';

const CartItem = (props) => {
  const { item } = props;
  return (
    <Row className="p-2 border-bottom">
      <Col md={10} className="">
        <Row>
          <Col md={3} className="d-flex flex-row-reverse">
            <img
              className="img-fluid rounded img-thumbnail"
              src={item.image}
              alt={item.name}
            />
          </Col>
          <Col className="d-flex flex-column">
            <ListGroup>
              <ListGroup.Item className="p-0 border-0">
                {item.name}
              </ListGroup.Item>
              <ListGroup.Item className="p-0 border-0">
                {item.countInStock !== 0 ? (
                  <p className="text-success small">In Stock</p>
                ) : (
                  <p className="text-danger">Unavailable</p>
                )}
              </ListGroup.Item>
            </ListGroup>
            <CartItemForm quantity={item.quantity} />
          </Col>
        </Row>
      </Col>
      <Col>
        <Stack gap={3}>
          <div className="d-flex flex-row-reverse">
            ${item.price.toFixed(2)}
          </div>
          <div></div>
          <Button
            className="ml-2 p-0 w-50"
            variant="light"
            size="sm"
            onClick={props.removeItem}
          >
            <i className="fa-solid fa-trash"></i>
          </Button>
        </Stack>
      </Col>
    </Row>
  );
};

export default CartItem;
