import React, { useState } from 'react';

import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';
import InputGroup from 'react-bootstrap/InputGroup';

const CartItemForm = (props) => {
  const [showNumInput, setShowNumInput] = useState(false);

  const showNumInputHandler = () => {
    setShowNumInput(true);
  };

  const quantityUpdateHandler = () => {};

  return (
    <Form>
      <Row>
        <Col md={3}>
          {showNumInput ? (
            <InputGroup size="sm">
              <Form.Control type="text" placeholder={props.quantity} />
              <Button
                variant="warning border border-1"
                onClick={quantityUpdateHandler}
              >
                <p className="m-0 small fw-lighter">Update</p>
              </Button>
            </InputGroup>
          ) : (
            <Dropdown className="dt d-inline-flex">
              <Dropdown.Toggle variant="light" size="sm">
                Qty: {props.quantity}
              </Dropdown.Toggle>
              <Dropdown.Menu className="dm" variant="light">
                <Dropdown.Item>0 (delete)</Dropdown.Item>
                <Dropdown.Item>1</Dropdown.Item>
                <Dropdown.Item>2</Dropdown.Item>
                <Dropdown.Item>3</Dropdown.Item>
                <Dropdown.Item>4</Dropdown.Item>
                <Dropdown.Item onClick={showNumInputHandler}>5+</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          )}
        </Col>
        <Col md={1}>
          <Button
            className="m-0 p-0"
            value="Delete"
            variant="light"
            type="submit"
          >
            <i className="fa-solid fa-trash"></i>
          </Button>
        </Col>
      </Row>
    </Form>
  );
};

export default CartItemForm;
