import { createContext, useReducer } from 'react';

export const Store = createContext();

const initialState = {
  cart: {
    cartItems: [],
  },
};

const reducer = (state, action) => {
  switch (action.type) {
    case 'CART_ADD_ITEM':
      // add to cart
      const newItem = action.payload;

      const existItem = state.cart.cartItems.find(
        (item) => item._id === newItem._id
      );

      const cartItems = existItem
        ? state.cart.cartItems.map((item) =>
            item._id === existItem._id ? newItem : item
          )
        : [...state.cart.cartItems, newItem];

      return { ...state, cart: { ...state.cart, cartItems }};
    case 'CART_UPDATE_ITEM_QUANTITY':
      const updatedQuantity = action.payload;
    case 'CART_REMOVE_ITEM':
      const itemId = action.id;
      console.log(state.cart.cartItems);
      const newCartItems = state.cart.cartItems.filter(item => item._id !== itemId);
      console.log("after " + newCartItems);
      return { ...state, cart: {...state.cart, newCartItems }};
    default:
      return initialState;
  }
};

export const StoreProvider = (props) => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const value = { state, dispatch };
  return <Store.Provider value={value}>{props.children}</Store.Provider>;
};
