import { useEffect, useReducer } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import logger from 'use-reducer-logger';
import Product from '../component/Product';

const INITIAL_STATE = {
	loading: true,
	error: '',
	products: [],
};

const reducer = (state, action) => {
	switch (action.type) {
		case 'FETCH_REQUEST':
			return { ...state, loading: true };
		case 'FETCH_SUCCESS':
			return { ...state, products: action.payload, loading: false };
		case 'FETCH_FAILURE':
			return { ...state, loading: false, error: action.payload };
		default:
			return state;
	}
};

const HomeScreen = () => {
	const [productState, dispatchProduct] = useReducer(
		logger(reducer),
		INITIAL_STATE
	);
	useEffect(() => {
		const fetchData = async () => {
			dispatchProduct({ type: 'FETCH_REQUEST' });
			try {
				const result = await axios.get('/api/products');
				dispatchProduct({ type: 'FETCH_SUCCESS', payload: result.data });
			} catch (err) {
				dispatchProduct({ type: 'FETCH_FAILURE', payload: err.message });
			}
		};
		fetchData();
	}, []);
	return (
		<div>
			<h1>Featured Products</h1>
			<div className="products">
				{productsState.loading ? (
					<div>Loading...</div>
				) : productsState.error ? (
					<div>{productsState.error}</div>
				) : (
					<Row>
						{productsState.products.map((product) => (
							<Col key={product.slug} sm={6} md={4} lg={3} className="mb-3">
								<Product product={product} />
							</Col>
						))}
					</Row>
				)}
			</div>
		</div>
	);
};

export default HomeScreen;
