import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet-async';

import { Store } from '../Store';
import CartItem from '../component/CartItem/CartItem';
import { Message } from '../component/Message';

import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ListGroup from 'react-bootstrap/ListGroup';

const CartScreen = () => {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const {
    cart: { cartItems },
  } = state;

  const updateQtyHandler = (item) => {
    ctxDispatch({ type: 'CART_UPDATE_ITEM_QUANTITY', payload: item });
  };

  const removeItemHandler = (id) => {
    ctxDispatch({ type: 'CART_REMOVE_ITEM', payload: id });
  };

  return (
    <Container>
      <Helmet>
        <title>Shopping Cart</title>
      </Helmet>
      <Row>
        <Col md={8}>
          <Card className="border-0">
            <Card.Title>Shopping Cart</Card.Title>
            {cartItems.length === 0 ? (
              <Message>
                Cart is empty. <Link to="/">Go Shopping</Link>
              </Message>
            ) : (
              <Card className="border-0">
                <Row className="border-bottom">
                  <Col md={10}></Col>
                  <Col className="d-flex flex-row-reverse">
                    <span>
                      <p className="m-0 fs-6 fw-lighter">Price</p>
                    </span>
                  </Col>
                </Row>
                {cartItems.map((item) => {
                  return (
                    <CartItem
                      key={item._id}
                      item={item}
                      removeItem={removeItemHandler}
                      updateQty={updateQtyHandler}
                    />
                  );
                })}
              </Card>
            )}
          </Card>
        </Col>
        <Col>
          {cartItems.length === 0 ? undefined : (
            <Card className="mt-4">
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <span className="m-0 lead">
                    Subtotal ({cartItems.reduce((a, c) => a + c.quantity, 0)}{' '}
                    items): 
                    <span className="fw-bold"> ${cartItems.reduce((a, c) => a + c.price * c.quantity, 0)}</span>
                  </span>
                </ListGroup.Item>
                <ListGroup.Item>
                  <div class="d-grid">
                    <Button
                      type="button"
                      variant="primary"
                      disabled={cartItems.length === 0}
                    >
                      Proceed to Checkout
                    </Button>
                  </div>
                </ListGroup.Item>
              </ListGroup>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default CartScreen;
