import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';

const CartItemForm = (props) => {
  return (
    <Form>
      <Row>
        <Col md={2}>
          <Dropdown className="dt d-inline-flex">
            <Dropdown.Toggle variant="light" size="sm">
              Qty: {props.quantity}
            </Dropdown.Toggle>

            <Dropdown.Menu className="dm " variant="light">
              <Dropdown.Item>0</Dropdown.Item>
              <Dropdown.Item>1</Dropdown.Item>
              <Dropdown.Item>2</Dropdown.Item>
              <Dropdown.Item>3</Dropdown.Item>
              <Dropdown.Item>4</Dropdown.Item>
              <Dropdown.Item>5</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
          <Button as={Col} md={2} className="m-0 p-0" value="Delete" variant="light" type="submit">
          <i class="fa-solid fa-trash-can"></i>
        </Button>
        </Col>

      </Row>
    </Form>
  );
};

export default CartItemForm;
