import axios from 'axios';
import { useEffect, useReducer } from 'react';
import { useParams } from 'react-router-dom';

const INITIAL_STATE = {
  product: {},
  loading: true,
  error: '',
};

const productReducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return { ...state, product: action.payload, loading: false };
    case 'FETCH_FAILURE':
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

const ProductScreen = (props) => {
  const params = useParams();
  const { slug } = params;
  const [productState, dispatchProduct] = useReducer(
    productReducer,
    INITIAL_STATE
  );

  useEffect(() => {
    const fetchData = async () => {
      dispatchProduct({ type: 'FETCH_REQUEST' });
      try {
        const result = await axios.get(`/api/products/slug/${slug}`);
        dispatchProduct({ type: 'FETCH_SUCCESS', payload: result.data });
      } catch (err) {
        dispatchProduct({ type: 'FETCH_FAILURE', payload: err.message });
      }
    };
    fetchData();
  }, [slug]);

  return productState.loading ? (
    <div>Loading...</div>
  ) : productState.error ? (
    <div>{productState.error}</div>
  ) : (
    <div>

    </div>
  );
};

export default ProductScreen;
