import React, { useState, useContext } from 'react';

import { Store } from '../../Store';

import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';
import InputGroup from 'react-bootstrap/InputGroup';

const CartItemForm = (props) => {
  const [qty, setQty] = useState('');
  const [showNumInput, setShowNumInput] = useState(false);

  const showNumInputHandler = () => {
    setShowNumInput(true);
  };

  return (
    <Form>
      <Row>
        <Col md={3}>
          {showNumInput ? (
            <InputGroup size="sm">
              <Form.Control type="text" placeholder={props.quantity} />
              <Button
                variant="warning border border-1"
                onClick={props.updateQty(qty)}
              >
                <p className="m-0 small fw-lighter">Update</p>
              </Button>
            </InputGroup>
          ) : (
            <Dropdown className="dt d-inline-flex">
              <Dropdown.Toggle variant="light" size="sm">
                Qty: {props.quantity}
              </Dropdown.Toggle>
              <Dropdown.Menu className="dm" variant="light">
                <Dropdown.Item eventKey="option-1">0 (delete)</Dropdown.Item>
                <Dropdown.Item eventKey="option-2">1</Dropdown.Item>
                <Dropdown.Item eventKey="option-">2</Dropdown.Item>
                <Dropdown.Item eventKey="option-">3</Dropdown.Item>
                <Dropdown.Item eventKey="option-">4</Dropdown.Item>
                <Dropdown.Item eventKey="option-" onClick={showNumInputHandler}>5+</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          )}
        </Col>
      </Row>
    </Form>
  );
};

export default CartItemForm;
