import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ListGroup from 'react-bootstrap/ListGroup';

const CartItem = (props) => {
  const { item } = props;
  return (
    <Row className="p-2 border-bottom">
      <Col md={10} className="">
        <Row>
          <Col
            md={3}
            className="d-flex flex-row-reverse"
            style={{ height: '180px' }}
          >
            <img
              className="img-thumbnail h-100"
              src={item.image}
              alt={item.name}
            />
          </Col>
          <Col>
            <ListGroup>
              <ListGroup.Item className="border-0">{item.name}</ListGroup.Item>
              <ListGroup.Item className="border-0">
                {item.countInStock !== 0 ? <p className="p-0 text-success">In Stock</p> : <p className="text-danger">Unavailable</p>}
              </ListGroup.Item>
            </ListGroup>
          </Col>
        </Row>
      </Col>
      <Col className="d-flex flex-row-reverse">
        <div>${item.price.toFixed(2)}</div>
      </Col>
    </Row>
  );
};

export default CartItem;
