import React, { useRef, useEffect, useState } from 'react';


import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';
import InputGroup from 'react-bootstrap/InputGroup';



const CartItemForm = (props) => {
  const qtyRef = useRef();
  const [quantity, setQuantity] = useState('');
  const [showNumInput, setShowNumInput] = useState(false);

  useEffect(() => {
    setQuantity(props.qty);
  }, [props.qty])

  const dropdownQtyHandler = (e) => {
    setQuantity(e);
    props.updateQty(props.item, e);
  };

  const textInputQtyHandler = () => {
    console.log(qtyRef.current.value);
    setQuantity(qtyRef.current.value);
    if (qtyRef.current.value < 5) {
      setShowNumInput(false);
    }
    console.log(qtyRef.current.value);
    props.updateQty(props.item, qtyRef.current.value);
  }

  const showNumInputHandler = () => {
    setShowNumInput(true);
  };

  return (
    <Row>
      <Col md={4}>
        {showNumInput ? (
          <Form onSubmit={textInputQtyHandler}>
            <InputGroup size="sm">
              <Form.Control ref={qtyRef} type="text" />
              <Button
                type="submit"
                variant="warning border border-1"
              >
                <p className="m-0 small fw-lighter">Update</p>
              </Button>
            </InputGroup>
          </Form>
        ) : (
          <Dropdown className="dt d-inline-flex" onSelect={dropdownQtyHandler}>
            <Dropdown.Toggle variant="light" size="sm">
              Qty: {quantity}
            </Dropdown.Toggle>
            <Dropdown.Menu className="dm" variant="light">
              <Dropdown.Item eventKey="0">0 (delete)</Dropdown.Item>
              <Dropdown.Item eventKey="1">1</Dropdown.Item>
              <Dropdown.Item eventKey="2">2</Dropdown.Item>
              <Dropdown.Item eventKey="3">3</Dropdown.Item>
              <Dropdown.Item eventKey="4">4</Dropdown.Item>
              <Dropdown.Item onClick={showNumInputHandler}>5+</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        )}
      </Col>
      {/* <Col>
        <Button onClick={}>Check Qty</Button>
      </Col> */}
    </Row>
  );
};

export default CartItemForm;
