import { useEffect, useReducer } from 'react';
import Row from '';
import { Link } from 'react-router-dom';
import axios from 'axios';
import logger from 'use-reducer-logger';

const INITIAL_STATE = {
	loading: true,
	error: '',
	products: [],
};

const reducer = (state, action) => {
	switch (action.type) {
		case 'FETCH_REQUEST':
			return { ...state, loading: true };
		case 'FETCH_SUCCESS':
			return { ...state, products: action.payload, loading: false };
		case 'FETCH_FAILURE':
			return { ...state, loading: false, error: action.payload };
		default:
			return state;
	}
};

const HomeScreen = () => {
	const [productsState, dispatchProducts] = useReducer(
		logger(reducer),
		INITIAL_STATE
	);
	useEffect(() => {
		const fetchData = async () => {
			dispatchProducts({ type: 'FETCH_REQUEST' });
			try {
				const result = await axios.get('/api/products');
				dispatchProducts({ type: 'FETCH_SUCCESS', payload: result.data });
			} catch (err) {
				dispatchProducts({ type: 'FETCH_FAILURE', payload: err.message });
			}
		};
		fetchData();
	}, []);
	return (
		<div>
			<h1>Featured Products</h1>
			<div className="products">
				{productsState.loading ? (
					<div>Loading...</div>
				) : productsState.error ? (
					<div>{productsState.error}</div>
				) : (
					<Row>
						{productsState.products.map((product) => (
							<div className="product" key={product.slug}>
								<Link to={`/product/${product.slug}`}>
									<img src={product.image} alt={product.name} />
								</Link>
								<div className="product-info">
									<Link to={`/product/${product.slug}`}>
										<p>{product.name}</p>
									</Link>
									<p>
										<strong>{product.price}</strong>
									</p>
									<button>Add to cart</button>
								</div>
							</div>
						))}
					</Row>
				)}
			</div>
		</div>
	);
};

export default HomeScreen;
